import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import { ProfileDb } from '../../config/profileData';
import Chip from '../../components/common/Chip';
import EmptyList from '../../components/common/EmptyList';

import { Link } from 'react-router-dom';

const Profile = () => {
  const { id } = useParams(5);
  const [profile, setBlog] = useState(null);

  useEffect(() => {
    let profile = ProfileDb.find((profile) => profile.id === parseInt(id));
    if (profile) {
      setBlog(profile);
    }
  }, []);

  return (
    <>
      <Link className='blog-goBack' to='/'>
        Anasayfa
      </Link>
      {profile ? (
        <div className='blog-wrap'>
          <header>
            <h1>{profile.AuthorName}</h1>
          </header>
          <img src={profile.AuthorImage} alt='cover' />
          <p className='blog-desc'>{profile.AuthorBio}</p>
        </div>
      ) : (
        <EmptyList />
      )}
    </>
  );
};

export default Profile;
